const DPOW_SIGCHANNEL: u32 = b's' as u32 | (b'i' as u32) << 8 | (b'g' as u32) << 16 | (b's' as u32) << 24;
const DPOW_SIGBTCCHANNEL: u32 = !DPOW_SIGCHANNEL;
const DPOW_TXIDCHANNEL: u32 = b't' as u32 | (b'x' as u32) << 8 | (b'i' as u32) << 16 | (b'd' as u32) << 24;
const DPOW_BTCTXIDCHANNEL: u32 = !DPOW_TXIDCHANNEL;


#[derive(Clone, Debug, Serialize, Deserialize, PartialEq)]
#[repr(C)]
struct IguanaPacketHeader {
    sigr: [u8; 32],
    sigs: [u8; 32],
    packethash: [u8; 32],
    nonce: u32,
    packetlen: u32,
}

#[derive(Clone, Debug, Serialize, Deserialize, PartialEq)]
#[repr(C)]
struct DpowNanoUtxo {
    srcutxo: [u8; 32],
    destutxo: [u8; 32],
    bestmask: [u8; 8],
    recvmask: [u8; 8],
    pendingcrc1: u32,
    pendingcrc2: u32,
    paxwdcrc: u32,
    srcvout: u16,
    destvout: u16,
    #[serde(with = "BigArray")]
    sig1: [u8; 128],
    #[serde(with = "BigArray")]
    sig2: [u8; 128],
    siglens: [u8; 2],
    pad: u8,
    bestk: u8,
}


#[derive(Clone, Debug, Serialize, Deserialize, PartialEq)]
#[repr(C)]
struct DpowNanoMsgHdr {
    srchash: [u8; 32],
    desthash: [u8; 32],
    ratify: DpowNanoUtxo,
    notarize: DpowNanoUtxo,
    channel: u32,
    height: u32,
    size: u32,
    datalen: u32,
    crc32: u32,
    myipbits: [u8; 4],
    numipbits: u32,
    #[serde(with = "BigArray")]
    ipbits: [u32; 512],
    symbol: [u8; 16],
    senderind: u8,
    senderind2: u8,
    version1: u8,
}

#[derive(Clone, Debug, Serialize, Deserialize, PartialEq)]
#[repr(C)]
struct dpow_sigentry {
    beacon: [u8; 32],
    mask:  [u8; 8],
    refcount: i32,
    senderind: u8,
    lastk: u8,
    siglen: u8,
    #[serde(with = "BigArray")]
    sig: [u8; 128],
    #[serde(with = "BigArray")]
    senderpub: [u8; 33]
}

fn print_hex(bytes: &[u8]) {
    for b in bytes {
        print!("{:02X}", b);
    }
    println!("");
}

